
      countDownDate = new Date("Feb 17, 2024 19:00:00").getTime(),
      x = setInterval(function () {
        var now = new Date().getTime(),
          distance = countDownDate - now,
          days = Math.floor(distance / 864e5),
          hours = Math.floor((distance % 864e5) / 36e5),
          minutes = Math.floor((distance % 36e5) / 6e4),
          seconds = Math.floor((distance % 6e4) / 1e3);
        (document.getElementById("days").innerHTML =
          days + "<small>days</small> "),
          (document.getElementById("hours").innerHTML =
            hours + "<small>hrs</small> "),
          (document.getElementById("minutes").innerHTML =
            minutes + "<small>mins</small> "),
          (document.getElementById("seconds").innerHTML =
            seconds + "<small>secs</small> "),
          distance < 0 &&
            (clearInterval(x),
            (document.getElementById("demo").innerHTML =
              "Wohoo! Celebrations"));
      }, 1e3);
   